ValidationRule.errorConditionFormula:
AND(
    ISPICKVAL(Status, "Closed"),
    OR(
    RecordType.Name = "Onboarding Pipeline",
    RecordType.DeveloperName = "New_Asset"
    ),
    OR ( ISBLANK(Bill__c),
        AND (  
            Bill__r.Start_Date_Pending__c = FALSE, 
            ISBLANK(Bill__r.Billing_Start_Date__c)
        )
    )
)
