ValidationRule.errorConditionFormula:
AND(
    OR(
        ISNEW(),
        ISCHANGED(StageName)
    ),
    OR(
        /* Wallet Opportunity */
        AND(
            RecordType.DeveloperName == 'Wallet_Opportunity',
            ISPICKVAL(Wallet_Opportunity_Type__c,'Coinbase Wallet Integration')
        )
    ),
    ISPICKVAL(StageName,'Closed Won'),
    !Survey_Sent__c,
    !Already_Connected__c
)
